import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

// MUI Imports
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import CircularProgress from '@mui/material/CircularProgress';
import Grid from '@mui/material/Grid';
import Paper from '@mui/material/Paper';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';

function Home({ onSignOut }) {
    const [resumeFile, setResumeFile] = useState(null);
    const [jobDescriptionFile, setJobDescriptionFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    const onResumeDrop = (acceptedFiles) => {
        const uploadedFile = acceptedFiles[0];
        if (uploadedFile) {
            setResumeFile(uploadedFile);
            setError(null);
        }
    };

    const onJobDescriptionDrop = (acceptedFiles) => {
        const uploadedFile = acceptedFiles[0];
        if (uploadedFile) {
            setJobDescriptionFile(uploadedFile);
            setError(null);
        }
    };

    const { getRootProps: getResumeRootProps, getInputProps: getResumeInputProps, isDragActive: isResumeDragActive } = useDropzone({
        onDrop: onResumeDrop,
        accept: {
            'application/pdf': ['.pdf'],
            'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
        }
    });

    const { getRootProps: getJobDescRootProps, getInputProps: getJobDescInputProps, isDragActive: isJobDescDragActive } = useDropzone({
        onDrop: onJobDescriptionDrop,
        accept: {
            'application/pdf': ['.pdf'],
            'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
        }
    });

    const handleResumeAnalysis = async () => {
        if (!resumeFile) {
            setError("Please upload a resume file to analyze.");
            return;
        }
        setLoading(true);
        setError(null);
        const formData = new FormData();
        formData.append('resume', resumeFile);

        try {
            const response = await axios.post('http://localhost:5000/api/analyze-resume', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            navigate('/analysis', { state: { analysis: response.data } });
        } catch (err) {
            console.error("Analysis error:", err);
            setError("Failed to analyze resume. Please check the backend server.");
        } finally {
            setLoading(false);
        }
    };

    const handleJobDescriptionAnalysis = async () => {
        if (!jobDescriptionFile) {
            setError("Please upload a job description file to analyze.");
            return;
        }
        setLoading(true);
        setError(null);
        const formData = new FormData();
        formData.append('jobDescription', jobDescriptionFile);
        if (resumeFile) {
            formData.append('resume', resumeFile); // Optionally include resume for comparison
        }

        try {
            const response = await axios.post('http://localhost:5000/api/analyze-job-description', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            navigate('/analysis', { state: { analysis: response.data } });
        } catch (err) {
            console.error("Analysis error:", err);
            setError("Failed to analyze job description. Please check the backend server.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box sx={{ p: 4 }}>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2, mb: 2 }}>
                <Button variant="outlined" onClick={onSignOut}>Logout</Button>
                <Button variant="outlined" onClick={() => alert("Help text here.")}>Help</Button>
            </Box>

            <Grid container spacing={4} sx={{ mt: 2 }}>
                <Grid item xs={12} md={6}>
                    <Paper elevation={3} sx={{ p: 3, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <Typography variant="h5" component="h3" gutterBottom>Upload Resume</Typography>
                        <Box
                            {...getResumeRootProps()}
                            sx={{
                                border: '2px dashed #ccc',
                                borderRadius: '4px',
                                p: 4,
                                width: '100%',
                                textAlign: 'center',
                                cursor: 'pointer',
                                '&:hover': {
                                    borderColor: '#007bff',
                                },
                            }}
                        >
                            <input {...getResumeInputProps()} />
                            <CloudUploadIcon fontSize="large" sx={{ color: '#007bff', mb: 1 }} />
                            {isResumeDragActive ? (
                                <Typography>Drop the resume file here ...</Typography>
                            ) : (
                                <Typography>Drag 'n' drop a resume file, or click to select one</Typography>
                            )}
                        </Box>
                        {resumeFile && <Typography variant="body1" sx={{ mt: 2 }}>Selected file: <strong>{resumeFile.name}</strong></Typography>}
                        <Button
                            variant="contained"
                            sx={{ mt: 3 }}
                            onClick={handleResumeAnalysis}
                            disabled={loading || !resumeFile}
                        >
                            {loading ? <CircularProgress size={24} color="inherit" /> : 'Analyze Resume'}
                        </Button>
                    </Paper>
                </Grid>

                <Grid item xs={12} md={6}>
                    <Paper elevation={3} sx={{ p: 3, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                        <Typography variant="h5" component="h3" gutterBottom>Upload Job Description</Typography>
                        <Box
                            {...getJobDescRootProps()}
                            sx={{
                                border: '2px dashed #ccc',
                                borderRadius: '4px',
                                p: 4,
                                width: '100%',
                                textAlign: 'center',
                                cursor: 'pointer',
                                '&:hover': {
                                    borderColor: '#007bff',
                                },
                            }}
                        >
                            <input {...getJobDescInputProps()} />
                            <CloudUploadIcon fontSize="large" sx={{ color: '#007bff', mb: 1 }} />
                            {isJobDescDragActive ? (
                                <Typography>Drop the job description file here ...</Typography>
                            ) : (
                                <Typography>Drag 'n' drop a job description file, or click to select one</Typography>
                            )}
                        </Box>
                        {jobDescriptionFile && <Typography variant="body1" sx={{ mt: 2 }}>Selected file: <strong>{jobDescriptionFile.name}</strong></Typography>}
                        <Button
                            variant="contained"
                            sx={{ mt: 3 }}
                            onClick={handleJobDescriptionAnalysis}
                            disabled={loading || !jobDescriptionFile}
                        >
                            {loading ? <CircularProgress size={24} color="inherit" /> : 'Analyze Job Description'}
                        </Button>
                    </Paper>
                </Grid>
            </Grid>
            
            {error && <Typography color="error" sx={{ mt: 2, textAlign: 'center' }}>{error}</Typography>}
        </Box>
    );
}

export default Home;
