// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';
import Home from './components/Home';
import AnalysisResults from './components/AnalysisResults';

function App() {
    // Manage authentication state with a state hook
    const [isAuthenticated, setIsAuthenticated] = useState(localStorage.getItem('isLoggedIn') === 'true');
    const [isSignInView, setIsSignInView] = useState(true);

    const handleSignInSuccess = () => {
        localStorage.setItem('isLoggedIn', 'true');
        setIsAuthenticated(true);
    };

    const handleSignOut = () => {
        localStorage.removeItem('isLoggedIn');
        setIsAuthenticated(false);
    };

    return (
        <Router>
            <div className="App">
                <header className="App-header">
                    <h1>Resume Analyzer</h1>
                </header>
                <Routes>
                    {/* Redirects to home if authenticated, otherwise to signin */}
                    <Route path="/" element={isAuthenticated ? <Navigate to="/home" /> : <Navigate to="/signin" />} />

                    {/* Pass the state-setting function to SignIn and SignUp */}
                    <Route path="/signin" element={isSignInView ? <SignIn onSignInSuccess={handleSignInSuccess} onSwitchToSignUp={() => setIsSignInView(false)} /> : <Navigate to="/signup" />} />

                    <Route path="/signup" element={isSignInView ? <Navigate to="/signin" /> : <SignUp onSwitchToSignIn={() => setIsSignInView(true)} />} />

                    {/* Protected routes */}
                    <Route path="/home" element={isAuthenticated ? <Home onSignOut={handleSignOut} /> : <Navigate to="/signin" />} />
                    <Route path="/analysis" element={isAuthenticated ? <AnalysisResults /> : <Navigate to="/signin" />} />

                    <Route path="*" element={<h1>404 Not Found</h1>} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
