// src/components/ResumeAnalyzer.js

import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

// Add 'onSignOut' to the props
function ResumeAnalyzer({ onSignOut }) { 
    const [file, setFile] = useState(null);
    const [analysis, setAnalysis] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const onDrop = (acceptedFiles) => {
        const uploadedFile = acceptedFiles[0];
        if (uploadedFile) {
            setFile(uploadedFile);
            setAnalysis(null);
            setError(null);
        }
    };

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: {
            'application/pdf': ['.pdf'],
            'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
        }
    });

    const handleAnalyze = async () => {
        if (!file) {
            setError("Please select a file to analyze.");
            return;
        }
        
        setLoading(true);
        setError(null);
        const formData = new FormData();
        formData.append('resume', file);

        try {
            const response = await axios.post('http://localhost:5000/api/analyze-resume', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setAnalysis(response.data);
        } catch (err) {
            console.error("Analysis error:", err);
            setError("Failed to analyze resume. Please check the backend server.");
            setAnalysis(null);
        } finally {
            setLoading(false);
        }
    };
    
    // Function to handle the help button click
    const handleHelp = () => {
        alert("This is a resume analysis tool. Upload a PDF or DOCX file to get insights on your skills and career path.");
    };

    return (
        <main>
            <div className="button-container">
                <button onClick={onSignOut} className="header-button">Logout</button>
                <button onClick={handleHelp} className="header-button">Help</button>
            </div>
            <div className="upload-container">
                <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
                    <input {...getInputProps()} />
                    {isDragActive ? (
                        <p>Drop the files here ...</p>
                    ) : (
                        <p>Drag 'n' drop a resume file here, or click to select one</p>
                    )}
                </div>
                {file && (
                    <div className="file-info">
                        <p>Selected file: <strong>{file.name}</strong></p>
                        <button onClick={handleAnalyze} disabled={loading}>
                            {loading ? 'Analyzing...' : 'Analyze Resume'}
                        </button>
                    </div>
                )}
            </div>
            {error && <div className="error-message">{error}</div>}
            {analysis && (
                <div className="analysis-results">
                    <h2>Analysis Results</h2>
                    <h3>Skills</h3>
                    <ul>
                        {analysis.skills.map((skill, index) => (
                            <li key={index}>{skill}</li>
                        ))}
                    </ul>
                    <h3>Job Matches</h3>
                    {analysis.jobMatches.length > 0 ? (
                        <ul>
                            {analysis.jobMatches.map((job, index) => (
                                <li key={index}>
                                    <strong>{job.title}</strong>: {job.matchPercentage}% match
                                    {job.matchingSkills.length > 0 && (
                                        <p>Matching skills: {job.matchingSkills.join(', ')}</p>
                                    )}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No job matches found based on your skills.</p>
                    )}
                    <h3>Skill Gaps</h3>
                    {analysis.skillGaps.length > 0 ? (
                        <ul>
                            {analysis.skillGaps.map((gap, index) => (
                                <li key={index}>{gap}</li>
                            ))}
                        </ul>
                    ) : (
                        <p>No significant skill gaps found.</p>
                    )}
                    <h3>Course Recommendations</h3>
                    {analysis.recommendations.length > 0 ? (
                        <ul>
                            {analysis.recommendations.map((rec, index) => (
                                <li key={index}>
                                    <strong>{rec.skill}</strong>: {rec.course}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No course recommendations at this time.</p>
                    )}
                </div>
            )}
        </main>
    );
}

export default ResumeAnalyzer;
