// src/components/SignIn.js
import React, { useState } from 'react';
import axios from 'axios';

function SignIn({ onSignInSuccess, onSwitchToSignUp }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleSignIn = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        try {
            await axios.post('http://localhost:5000/api/login', { username, password }, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            // Call the prop to notify the parent about successful sign-in
            onSignInSuccess(); 

        } catch (err) {
            setError(err.response?.data?.error || 'Login failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <h2>Sign In</h2>
            <form onSubmit={handleSignIn}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit" disabled={loading}>
                    {loading ? 'Signing In...' : 'Sign In'}
                </button>
            </form>
            {error && <p className="error-message">{error}</p>}
            <p>
                Don't have an account?{' '}
                <button className="link-button" onClick={onSwitchToSignUp}>
                    Sign Up
                </button>
            </p>
        </div>
    );
}

export default SignIn;
