import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import './App.css';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';
import Home from './components/Home';
import AnalysisResults from './components/AnalysisResults';

// A simple wrapper to protect routes
function PrivateRoute({ children, isAuthenticated }) {
    const location = useLocation();
    return isAuthenticated ? children : <Navigate to="/signin" state={{ from: location }} replace />;
}

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isSignInView, setIsSignInView] = useState(true);

    const handleSignInSuccess = () => {
        setIsAuthenticated(true);
    };

    const handleSignOut = () => {
        setIsAuthenticated(false);
        // You can add logic here to clear any user tokens if you were using them
    };

    if (!isAuthenticated) {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>Resume Analyzer</h1>
                </header>
                {isSignInView ? (
                    <SignIn onSignInSuccess={handleSignInSuccess} onSwitchToSignUp={() => setIsSignInView(false)} />
                ) : (
                    <SignUp onSwitchToSignIn={() => setIsSignInView(true)} />
                )}
            </div>
        );
    }


    return (
        <Router>
            <div className="App">
                <header className="App-header">
                    <h1>Resume Analyzer</h1>
                </header>
                <Routes>
                    {/* Public routes */}
                    <Route path="/signin" element={<SignIn onSignInSuccess={handleSignInSuccess} />} />
                    <Route path="/signup" element={<SignUp />} />

                    {/* Private routes (protected by the PrivateRoute component) */}
                    <Route
                        path="/home"
                        element={
                            <PrivateRoute isAuthenticated={isAuthenticated}>
                                <Home onSignOut={handleSignOut} />
                            </PrivateRoute>
                        }
                    />
                    <Route
                        path="/analysis"
                        element={
                            <PrivateRoute isAuthenticated={isAuthenticated}>
                                <AnalysisResults />
                            </PrivateRoute>
                        }
                    />

                    {/* Redirects any other path to the correct starting point */}
                    <Route path="*" element={<Navigate to={isAuthenticated ? "/home" : "/signin"} />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
