// src/components/Home.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

function Home() {
    const [file, setFile] = useState(null);
    const [jobDescription, setJobDescription] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    const onDrop = (acceptedFiles) => {
        const uploadedFile = acceptedFiles[0];
        if (uploadedFile) {
            setFile(uploadedFile);
            setError(null);
        }
    };

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: {
            'application/pdf': ['.pdf'],
            'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
        }
    });

    const handleAnalyze = async () => {
        if (!file || !jobDescription) {
            setError("Please upload a resume and provide a job description.");
            return;
        }

        setLoading(true);
        setError(null);
        const formData = new FormData();
        formData.append('resume', file);
        formData.append('jobDescription', jobDescription); // Add job description to form data

        try {
            const response = await axios.post('http://localhost:5000/api/analyze-resume', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            // Pass analysis data to the new page
            navigate('/analysis', { state: { analysis: response.data } });
        } catch (err) {
            console.error("Analysis error:", err);
            setError("Failed to analyze resume. Please check the backend server.");
        } finally {
            setLoading(false);
        }
    };

    const handleSignOut = () => {
        localStorage.removeItem('isLoggedIn');
        navigate('/signin');
    };

    const handleHelp = () => {
         alert("Upload your resume and a job description. The tool will analyze your skills against the job requirements and provide insights.");
    };

    return (
        <main>
            <div className="button-container">
                <button onClick={handleSignOut} className="header-button">Logout</button>
                <button onClick={handleHelp} className="header-button">Help</button>
            </div>
            <div className="upload-container">
                <div {...getRootProps()} className={`dropzone ${isDragActive ? 'active' : ''}`}>
                    <input {...getInputProps()} />
                    {isDragActive ? (
                        <p>Drop the resume file here ...</p>
                    ) : (
                        <p>Drag 'n' drop a resume file here, or click to select one</p>
                    )}
                </div>
                {file && <p className="file-info">Selected file: <strong>{file.name}</strong></p>}

                <textarea
                    className="job-description-box"
                    placeholder="Paste the job description here..."
                    value={jobDescription}
                    onChange={(e) => setJobDescription(e.target.value)}
                ></textarea>

                <button onClick={handleAnalyze} disabled={loading} className="analyze-button">
                    {loading ? 'Analyzing...' : 'Analyze'}
                </button>
                {error && <div className="error-message">{error}</div>}
            </div>
        </main>
    );
}

export default Home;
