// src/App.js

import React, { useState } from 'react';
import './App.css';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';
import ResumeAnalyzer from './components/ResumeAnalyzer';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isSignInView, setIsSignInView] = useState(true);

    const handleSignInSuccess = () => {
        setIsAuthenticated(true);
    };

    const handleSignOut = () => {
        setIsAuthenticated(false);
        // You can add logic here to clear any user tokens if you were using them
    };

    if (!isAuthenticated) {
        return (
            <div className="App">
                <header className="App-header">
                    <h1>Resume Analyzer</h1>
                </header>
                {isSignInView ? (
                    <SignIn onSignInSuccess={handleSignInSuccess} onSwitchToSignUp={() => setIsSignInView(false)} />
                ) : (
                    <SignUp onSwitchToSignIn={() => setIsSignInView(true)} />
                )}
            </div>
        );
    }

    return (
        <div className="App">
            <header className="App-header">
                <h1>Resume Analyzer</h1>
                {/* Pass handleSignOut as a prop to ResumeAnalyzer */}
                <ResumeAnalyzer onSignOut={handleSignOut} />
            </header>
        </div>
    );
}

export default App;
